INSERT INTO blocks VALUES ('index','print \"\\n\\n<!-- begin index block -->\\n\\n\";\r\n\r\nif ($I{U}{uid} > 0) {\r\n	printf qq[<FONT SIZE=\"${\\( $I{fontbase} + 1 )}\"><B>This page was generated by a %s of %s %s for ] .\r\n		qq[<A HREF=\"$I{rootdir}/users.pl\">$I{U}{nickname}</A> ($I{U}{uid}).</B></FONT><P>\\n],\r\n		(\'Flock\', \'Swarm\', \'Barrel\', \'Team\', \'Squadron\', \'Group\', \'Cadre\')[rand 7],\r\n		(\'Rabid\', \'Uber\', \'Psycho\', \'Attack\', \'Circus\', \'Albino\', \'Trained\',\r\n			\'Stealth\', \'Super\', \'Elite\', \'Random\', \'Orange\', \'Ultra\')[rand 13],\r\n		(\'Mummies\', \'Elephants\', \'Midgets\', \'Ninjas\', \'Squirrels\',\r\n			\'Chickens\', \'Geese\', \'Monkeys\', \'Geese\')[rand 9],\r\n	$SECT->{artcount} = $I{U}{maxstories};\r\n}\r\n\r\n\r\nmy $stories = selectStories($SECT, $I{U}{maxstories});\r\ndisplayStories($stories);\r\n\r\nunless ($I{U}{noboxes}) {\r\n	print qq[\\n</TD>\\n<TD>&nbsp;</TD><TD WIDTH=\"210\" ALIGN=\"CENTER\" VALIGN=\"TOP\">\\n];\r\n	displayStandardBlocks($SECT, $stories);\r\n}\r\n\r\n$stories->finish if $stories;\r\n\r\nprint \"\\n\\n<!-- end index block -->\\n\\n\";\r\n\r\n',NULL,5000,'eval','This is the main block of html and code that gets evaled to create the main\r\nindex page.');
